mapper.update:
update tbl_board set
	title = #{title},
	writer = #{writer},
	content = #{content}
where 1=1
and bid = #{bid}

mapper.insert:
insert into tbl_board (
	title,
	writer,
	content
)value (
	#{title},
	#{writer},
	#{content}
)

delete from tbl_board
where 1=1
and bid = #{bid}
